import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";
import { useState, useEffect, useMemo } from "react";
import { get, post } from "../service/apiServices";
import { url } from "../utils/request";
import { Button } from "@mui/material";
const AddSize = () => {
  const [product, setProduct] = useState([]);
  const [images_color, setImageColors] = useState([]);
  useEffect(() => {
    const fetchProductApi = async () => {
      const result = await get("product/data");
      setProduct(result);
    };
    fetchProductApi();
  }, []);
  useEffect(() => {
    const fetchImageColorApi = async () => {
      const result = await get("product/color/data");
      setImageColors(result);
    };
    fetchImageColorApi();
  }, []);
  const [size, setSize] = useState({
    image_color: "",
    size_id: 0,
    quantity: 0,
    sell: 0,
  });
  console.log(size);
  return (
    <>
      <div>
        <h2 className="page-header">Add Size Product</h2>
        <div className="row">
          <div className="col-12">
            <div className="card">
              <div className="card__body">
                <Box sx={{ flexGrow: 1 }}>
                  <Grid container spacing={2}>
                    <Grid item xs={4}>
                      <Autocomplete
                        onChange={(event, newValue) => {
                          setSize({
                            ...size,
                            image_color: newValue === null ? null : newValue.image_color
                          })
                        }}
                        id="controllable-states-demo"
                        getOptionLabel={(images_color) =>
                          product.find((f) => f.id === images_color.product_id)
                            .name
                        }
                        options={images_color}
                        sx={{ width: "100%", background: "#fff" }}
                        isOptionEqualToValue={(option, value) =>
                          option.image_color === value.image_color
                        }
                        noOptionsText={"NO IMAGE"}
                        renderOption={(props, images_color) => (
                          <Box
                            component="li"
                            {...props}
                            key={images_color.image_color}
                          >
                            <img
                              style={{
                                border: "1px solid #ccc",
                                height: "50px",
                                width: "50px",
                                marginRight: "10px",
                              }}
                              src={url + images_color.image_color}
                              alt=""
                            />
                            <li>
                              {
                                product.find(
                                  (f) => f.id === images_color.product_id
                                ).name
                              }
                            </li>
                          </Box>
                        )}
                        renderInput={(params) => (
                          <TextField {...params} label="Image Color" />
                        )}
                      />
                    </Grid>
                    <Grid item xs={4}>
                      <TextField
                        value={size.size_id}
                        onChange={(e) =>
                          setSize({
                            ...size,
                            size_id: e.target.value,
                          })
                        }
                        style={{ width: "100%" }}
                        sx={{ input: { color: "black", background: "#fff" } }}
                        id="filled-basic"
                        label="Size Name"
                        variant="outlined"
                      />
                    </Grid>
                    <Grid item xs={4}>
                      <TextField
                        value={size.quantity}
                        onChange={(e) =>
                          setSize({
                            ...size,
                            quantity: e.target.value,
                          })
                        }
                        style={{ width: "100%" }}
                        sx={{ input: { color: "black", background: "#fff" } }}
                        id="filled-basic"
                        label="Quantity"
                        variant="outlined"
                      />
                    </Grid>
                  </Grid>
                  <Grid container spacing={2} sx={{ marginTop: "10px" }}>
                  <Grid item xs={6}>
                    <Button variant="outlined" onClick={handelAddProduct}>
                      Add Product
                    </Button>
                  </Grid>
                </Grid>
                </Box>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default AddSize;
