import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";
import { useState, useEffect } from "react";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
import Input from "@mui/material/Input";
import InputAdornment from "@mui/material/InputAdornment";
import { margin } from "@mui/system";
import DropFileInput from "../components/drop-file-input/DropFileInput";
const AddImage = () => {
  const product = [
    {
      product_id: 1,
      name: "Giày cổ thấp",
    },
    {
      product_id: 2,
      name: "Giày đinh",
    },
  ];
  const images_color = [
    {
      product_id: 1,
      image_color: "Giay_Co_Thap_Forum_trang_FY7756_01_standard-removebg-preview.png"
    },
    {
      product_id: 1,
      image_color: "Giay_Co_Thap_Forum_trang_FY7757_01_standard-removebg-preview.png"
    },
    {
      product_id: 2,
      image_color: "Giay_DJinh_Lien_BOA_Codechaos_22_DJen_GX3937_01_standard-removebg-preview.png"
    },
    {
      product_id: 2,
      image_color: "Giay_DJinh_Lien_BOA_Codechaos_22_trang_GX0199_01_standard-removebg-preview.png"
    },
    {
      product_id: 2,
      image_color: "Giay_DJinh_Lien_BOA_Codechaos_22_trang_GX3938_01_standard-removebg-preview.png"
    }
  ]
  const [productID, setProductID] = useState('')
  console.log(productID);
  const onFileChange = (files) => {
    files.map(file => {
      let image_color = file.name
      const data = { productID, image_color}
      // Fetch API
      console.log(data);
    })
  };
  return (
    <>
    <div>
      <h2 className="page-header">Add Image</h2>
      <div className="row">
        <div className="col-12">
          <div className="card">
            <div className="card__body">
              <Box sx={{ flexGrow: 1 }}>
                <Grid container spacing={2}>
                  <Grid item xs={8}>
                    <div className="box">
                      <DropFileInput onFileChange={onFileChange} />
                    </div>
                  </Grid>
                  <Grid item xs={4}>
                    <Autocomplete
                        onChange={(event, newValue) => {
                            setProductID(newValue === null ? null : newValue.product_id)
                        }}
                      id="controllable-states-demo"
                      getOptionLabel={(product) => `${product.name}`}
                      options={product}
                      sx={{ width: "100%", background: "#fff" }}
                      isOptionEqualToValue={(option, value) =>
                        option.product_id === value.product_id
                      }
                      noOptionsText={"NO BRAND"}
                      renderOption={(props, product) => (
                        <Box component="li" {...props} key={product.product_id}>
                          {product.name}
                        </Box>
                      )}
                      renderInput={(params) => (
                        <TextField {...params} label="Products" />
                      )}
                    />
                  </Grid>
                </Grid>
              </Box>
            </div>
          </div>
        </div>
      </div>
    </div>
    <div>
      <h2 className="page-header">Add Image Detail</h2>
      <div className="row">
        <div className="col-12">
          <div className="card">
            <div className="card__body">
              <Box sx={{ flexGrow: 1 }}>
                <Grid container spacing={2}>
                  <Grid item xs={8}>
                    <div className="box">
                      <DropFileInput onFileChange={onFileChange} />
                    </div>
                  </Grid>
                  <Grid item xs={4}>
                    <Autocomplete
                        onChange={(event, newValue) => {
                            setProductID(newValue === null ? null : newValue.product_id)
                        }}
                      id="controllable-states-demo"
                      getOptionLabel={(images_color) => ""}
                      options={images_color}
                      sx={{ width: "100%", background: "#fff" }}
                      isOptionEqualToValue={(option, value) =>
                        option.image_color === value.image_color
                      }
                      noOptionsText={"NO BRAND"}
                      renderOption={(props, images_color) => (
                        <Box component="div" {...props} key={images_color.product_id}>
                          <li></li>
                          <img width={"50px"} height={"50px"} style={{ border: "1px solid #ccc", height: "50px", width: "50px" }} src={`../images/product_img/${images_color.image_color}`} />
                        </Box>
                      )}
                      renderInput={(params) => (
                        <TextField {...params} label="Products" />
                      )}
                    />
                  </Grid>
                </Grid>
              </Box>
            </div>
          </div>
        </div>
      </div>
    </div>
    </>
  );
};
export default AddImage;
