import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";
import { useState, useEffect } from "react";
import { margin } from "@mui/system";

const AddProduct = () => {
  const [brands, setBrand] = useState([]);
  const [brandId, setBrandId] = useState();
  const brand = [
    {
      id: 1,
      brand: "Adidas",
    },
    {
      id: 2,
      brand: "Nike",
    },
  ];
  useEffect(() => {
    setBrand(brand);
  }, []);
  return (
    <div>
      <h2 className="page-header">Add Product</h2>
      <div className="row">
        <div className="col-12">
          <div className="card">
            <div className="card__body">
              <Box sx={{ flexGrow: 1 }}>
                <Grid container spacing={2}>
                  <Grid item xs={6}>
                    <TextField
                      style={{ width: "100%" }}
                      sx={{ input: { color: "black", background: "#fff" } }}
                      id="filled-basic"
                      label="Product Name"
                      variant="outlined"
                    />
                  </Grid>
                  <Grid item xs={6}>
                    <Autocomplete
                      onChange={(event, newValue) => {
                        setBrandId(newValue === null ? 0 : newValue.id);
                      }}
                      // inputValue={inputValue}
                      // onInputChange={(event, newInputValue) => {
                      //   setInputValue(newInputValue);
                      // }}
                      id="controllable-states-demo"
                      getOptionLabel={(brands) =>
                        `${brands.brand} ${brands.id}`
                      }
                      options={brand}
                      sx={{ width: "100%", background: "#fff" }}
                      isOptionEqualToValue={(option, value) =>
                        option.brand === value.brand
                      }
                      noOptionsText={"NO BRAND"}
                      renderOption={(props, brands) => (
                        <Box component="li" {...props} key={brands.id}>
                          {brands.brand} {brands.id}
                        </Box>
                      )}
                      renderInput={(params) => (
                        <TextField {...params} label="Search Brands" />
                      )}
                    />
                  </Grid>
                </Grid>
                <Grid container spacing={2} sx={{ marginTop: "10px" }}>
                  <Grid item xs={6}>
                    <TextField
                      style={{ width: "100%" }}
                      sx={{ input: { color: "black", background: "#fff" } }}
                      id="filled-basic"
                      label="Purchase Price"
                      variant="outlined"
                    />
                  </Grid>
                  <Grid item xs={6}>
                    <TextField
                      style={{ width: "100%" }}
                      sx={{ input: { color: "black", background: "#fff" } }}
                      id="filled-basic"
                      label="Price"
                      variant="outlined"
                    />
                  </Grid>
                </Grid>
              </Box>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
export default AddProduct;
