import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";
import DropFileInput from "../drop-file-input/DropFileInput";
import { useState, useEffect, memo } from "react";
import { get, post, postFile } from "../../service/apiServices";
import { url } from "../../utils/request";
const AddImageDetail = (props) => {
  const { product } = props;
  const [images_color, setImageColors] = useState([])
  useEffect(() => {
    const fetchBrandApi = async () => {
      const result = await get("product/color/data");
      console.log(result);
      setImageColors(result);
    };
    fetchBrandApi();
  }, [])
  // const images_color = [
  //   {
  //     product_id: 1,
  //     image_color:
  //       "Giay_Co_Thap_Forum_trang_FY7756_01_standard-removebg-preview.png",
  //   },
  //   {
  //     product_id: 1,
  //     image_color:
  //       "Giay_Co_Thap_Forum_trang_FY7757_01_standard-removebg-preview.png",
  //   },
  //   {
  //     product_id: 2,
  //     image_color:
  //       "Giay_DJinh_Lien_BOA_Codechaos_22_DJen_GX3937_01_standard-removebg-preview.png",
  //   },
  //   {
  //     product_id: 2,
  //     image_color:
  //       "Giay_DJinh_Lien_BOA_Codechaos_22_trang_GX0199_01_standard-removebg-preview.png",
  //   },
  //   {
  //     product_id: 2,
  //     image_color:
  //       "Giay_DJinh_Lien_BOA_Codechaos_22_trang_GX3938_01_standard-removebg-preview.png",
  //   },
  // ];
  const [image_color, setImagePath] = useState("");
  console.log(image_color);
  const onImageSubmit = (files) => {
    let formData = new FormData();
    files.map((file) => {
      let image_detail = file.name;
      const data = { image_color, image_detail };
      console.log(file);
      formData.append("file", file);
      const addFileApi = async () => {
        const result = await postFile(formData)
        console.log(result);
      }
      addFileApi()
      // Fetch API
      console.log(data);
      const addProductImage = async () => {
        const result = await post("product/colorDetail/save", data);
        console.log(result);
      };
      addProductImage();
    });
  };
  return (
    <div>
      <h2 className="page-header">Add Image Detail</h2>
      <div className="row">
        <div className="col-12">
          <div className="card">
            <div className="card__body">
              <Box sx={{ flexGrow: 1 }}>
                <Grid container spacing={2}>
                  <Grid item xs={8}>
                    <div className="box">
                      <DropFileInput
                        onImageSubmit={onImageSubmit}
                        name={"Add Image Detail"}
                      />
                    </div>
                  </Grid>
                  <Grid item xs={4}>
                    <Autocomplete
                      onChange={(event, newValue) => {
                        setImagePath(
                          newValue === null ? null : newValue.image_color
                        );
                      }}
                      id="image_color"
                      getOptionLabel={(images_color) =>
                        product.find(
                          (f) => f.id === images_color.product_id
                        ).name
                      }
                      options={images_color}
                      sx={{ width: "100%", background: "#fff" }}
                      isOptionEqualToValue={(option, value) =>
                        option.image_color === value.image_color
                      }
                      noOptionsText={"NO IMAGE"}
                      renderOption={(props, images_color) => (
                        <Box
                          component="div"
                          {...props}
                          key={images_color.image_color}
                        >
                          <img
                            style={{
                              border: "1px solid #ccc",
                              height: "50px",
                              width: "50px",
                              marginRight: "10px",
                            }}
                            src={url + images_color.image_color}
                            alt=""
                          />
                          <li>
                            {
                              product.find(
                                (f) => f.id === images_color.product_id
                              ).name
                            }
                          </li>
                        </Box>
                      )}
                      renderInput={(params) => (
                        <TextField {...params} label="Image Name" />
                      )}
                    />
                  </Grid>
                </Grid>
              </Box>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
export default memo(AddImageDetail);
