import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";
import { useState, useEffect, useMemo } from "react";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
import Input from "@mui/material/Input";
import InputAdornment from "@mui/material/InputAdornment";
import { margin } from "@mui/system";
import DropFileInput from "../components/drop-file-input/DropFileInput";
import AddImageDetail from "../components/addImage/AddImageDetail";
const AddImage = () => {
  const [product, setProduct] = useState([])
  useMemo(() => {
    setProduct([
      {
        product_id: 1,
        name: "Giày cổ thấp",
      },
      {
        product_id: 2,
        name: "Giày đinh",
      },
    ])
  }, [])
  const [productID, setProductID] = useState('')
  const [file, setFiles] = useState();  
  const onImageSubmit = (files) => {
    console.log(123123112312);
    setFiles(files)
    files.map(file => {
      console.log(file);
      let image_color = file.name
      const data = { productID, image_color}
      // Fetch API
      console.log(data);
    })
  };
  const handelInputChange = (e) => {
    console.log(e.target.file);
  }
  return (
    <>
    <div>
      <input type="file" onChange={handelInputChange} />
      <h2 className="page-header">Add Image</h2>
      <div className="row">
        <div className="col-12">
          <div className="card">
            <div className="card__body">
              <Box sx={{ flexGrow: 1 }}>
                <Grid container spacing={2}>
                  <Grid item xs={8}>
                    <div className="box">
                      <DropFileInput onImageSubmit={onImageSubmit} name={"Add Image Product"} />
                    </div>
                  </Grid>
                  <Grid item xs={4}>
                    <Autocomplete
                        onChange={(event, newValue) => {
                            setProductID(newValue === null ? null : newValue.product_id)
                        }}
                      id="controllable-states-demo"
                      getOptionLabel={(product) => `${product.name}`}
                      options={product}
                      sx={{ width: "100%", background: "#fff" }}
                      isOptionEqualToValue={(option, value) =>
                        option.product_id === value.product_id
                      }
                      noOptionsText={"NO BRAND"}
                      renderOption={(props, product) => (
                        <Box component="li" {...props} key={product.product_id}>
                          {product.name}
                        </Box>
                      )}
                      renderInput={(params) => (
                        <TextField {...params} label="Products" />
                      )}
                    />
                  </Grid>
                </Grid>
              </Box>
            </div>
          </div>
        </div>
      </div>
    </div>
    <AddImageDetail product={product} file={file} />
    </>
  );
};
export default AddImage;
