import React from 'react'

import Table from '../components/table/Table'
import List from '../components/list_img/List'
import customerList from '../assets/JsonData/customers-list.json'
import { products } from '../assets/JsonData/products'
import { image_color } from '../assets/JsonData/image_color'
import { useState, useEffect, useRef } from 'react'

const Products = () => {
    const [product, setProduct] = useState([]);
    const [images_color, setImageColors] = useState([]);
      // list product
  useEffect(() => {
    const fetchProductApi = async () => {
      const result = await get("product/data");
      setProduct(result);
    };
    fetchProductApi();
  }, []);
  console.log(product);
  // list image product
  useEffect(() => {
    const fetchImageColorApi = async () => {
      const result = await get("product/color/data");
      setImageColors(result);
    };
    fetchImageColorApi();
  }, []);
    const customerTableHead = useRef([
        '',
        'name',
        'image',
        'brand',
        'Staff Name',
        'Create At',
        'Update At',
        'Purchase Price',
        'Price',
        'Status',
        'Total order',
        '',
        ''
        
    ])
    const renderBody = (item, index) => (
        <tr key={index}>
            <td>{item.id}</td>
            <td>{item.name}</td>
            <List id={item.id} bodyData={image_color} />
            <td>{item.brand}</td>
            <td>{item.Staff_Name}</td>
            <td>{item.Create_At}</td>
            <td>{item.Update_At}</td>
            <td>{item.purchase_price}</td>
            <td>{item.price}</td>
            <td>{item.status}</td>
            <td>{item.total_orders}</td>
            <td style={{cursor: 'pointer'}}><i class='bx bx-pencil'></i></td>
            <td style={{cursor: 'pointer'}}><i class='bx bx-trash-alt' ></i></td>
        </tr>
    )
    return (
        <div>
            <h2 className="page-header">
                Products
            </h2>
            <div className="row">
                <div className="col-12">
                    <div className="card">
                        <div className="card__body">
                            <Table
                                limit='10'
                                headData={customerTableHead.current}
                                bodyData={products}
                                renderBody={(item, index) => renderBody(item, index)}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Products
