import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";
import DropFileInput from "../drop-file-input/DropFileInput";
const AddImageDetail = () => {

  const images_color = [
    {
      product_id: 1,
      image_color:
        "Giay_Co_Thap_Forum_trang_FY7756_01_standard-removebg-preview.png",
    },
    {
      product_id: 1,
      image_color:
        "Giay_Co_Thap_Forum_trang_FY7757_01_standard-removebg-preview.png",
    },
    {
      product_id: 2,
      image_color:
        "Giay_DJinh_Lien_BOA_Codechaos_22_DJen_GX3937_01_standard-removebg-preview.png",
    },
    {
      product_id: 2,
      image_color:
        "Giay_DJinh_Lien_BOA_Codechaos_22_trang_GX0199_01_standard-removebg-preview.png",
    },
    {
      product_id: 2,
      image_color:
        "Giay_DJinh_Lien_BOA_Codechaos_22_trang_GX3938_01_standard-removebg-preview.png",
    },
  ];
  const onImageSubmit = (files) => {
    files.map((file) => {
      let image_color = file.name;
    });
  };
  return (
    <div>
    <h2 className="page-header">Add Image Detail</h2>
    <div className="row">
      <div className="col-12">
        <div className="card">
          <div className="card__body">
            <Box sx={{ flexGrow: 1 }}>
              <Grid container spacing={2}>
                <Grid item xs={8}>
                  <div className="box">
                    <DropFileInput onImageSubmit={onImageSubmit} />
                  </div>
                </Grid>
                <Grid item xs={4}>
                  <Autocomplete
                    // onChange={(event, newValue) => {
                    //   setProductID(
                    //     newValue === null ? null : newValue.product_id
                    //   );
                    // }}
                    id="image_color"
                    // getOptionLabel={(images_color) =>
                    //   product.find(
                    //     (f) => f.product_id === images_color.product_id
                    //   ).name
                    // }
                    options={images_color}
                    sx={{ width: "100%", background: "#fff" }}
                    isOptionEqualToValue={(option, value) =>
                      option.image_color === value.image_color
                    }
                    noOptionsText={"NO BRAND"}
                    renderOption={(props, images_color) => (
                      <Box
                        component="div"
                        {...props}
                        key={images_color.product_id}
                      >
                        <img
                          style={{
                            border: "1px solid #ccc",
                            height: "50px",
                            width: "50px",
                            marginRight: "10px",
                          }}
                          src={`../images/product_img/${images_color.image_color}`}
                        />
                        {/* <li>
                          {
                            product.find(
                              (f) => f.product_id === images_color.product_id
                            ).name
                          }
                        </li> */}
                      </Box>
                    )}
                    renderInput={(params) => (
                      <TextField {...params} label="Image Name" />
                    )}
                  />
                </Grid>
              </Grid>
            </Box>
          </div>
        </div>
      </div>
    </div>
  </div>
  )

};
export default AddImageDetail;
