import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import TextField from "@mui/material/TextField";
import { useState, useEffect } from "react";
import Button from "@mui/material/Button";
import { get, post } from "../service/apiServices";
import Table from "../components/table/Table";
const customerTableHead = ["", "name"];

const renderHead = (item, index) => <th key={index}>{item}</th>;

const renderBody = (item, index) => (
  <tr key={index}>
    <td>{item.id}</td>
    <td>{item.name}</td>
  </tr>
);
const AddBrand = () => {
  const [brands, setBrands] = useState("");
  const [name, setName] = useState("");
  const [load, setLoad] = useState("");
  const handelAddBrand = () => {
    const addBrandApi = async () => {
      const result = await post("brand/save", name);
      console.log(result);
      setName("");
      setLoad((prev) => [...prev, name]);
    };
    addBrandApi();
  };
  useEffect(() => {
    const fetchBrandApi = async () => {
      const result = await get("brand/data");
      console.log(result);
      setBrands(result);
    };
    fetchBrandApi();
  }, [load]);
  return (
    <div>
      <h2 className="page-header">Add Brand</h2>
      <div className="row">
        <div className="col-4">
          <div className="card">
            <div className="card__body">
              <Box sx={{ flexGrow: 1 }}>
                <Grid container spacing={2}>
                  <Grid item xs={12}>
                    <TextField
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                      style={{ width: "100%" }}
                      sx={{ input: { color: "black", background: "#fff" } }}
                      id="filled-basic"
                      label="Brand"
                      variant="outlined"
                    />
                  </Grid>
                </Grid>
                <Grid container spacing={2} sx={{ marginTop: "10px" }}>
                  <Grid item xs={6}>
                    <Button variant="outlined" onClick={handelAddBrand}>
                      Add Brand
                    </Button>
                  </Grid>
                </Grid>
              </Box>
            </div>
          </div>
        </div>
        <div className="col-8">
        <Grid item xs={9}>
                    <div>
                      <div className="row">
                        <div className="col-12">
                          <div className="card">
                            <div className="card__body">
                              <Table
                                limit="10"
                                headData={customerTableHead}
                                renderHead={(item, index) =>
                                  renderHead(item, index)
                                }
                                bodyData={brands}
                                renderBody={(item, index) =>
                                  renderBody(item, index)
                                }
                              />
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </Grid>
        </div>
      </div>
    </div>
  );
};

export default AddBrand;
